import React, { useState, useEffect } from 'react';
import Header from './Header.jsx';
import NavBar from './NavBar.jsx';
import Dashboard from '/DashBoard.jsx'; // Match the exact file name
import ExpenseDashboard from './ExpenseDashboard.jsx';
import ProjectForm from './ProjectForm.jsx';
import ExpenseForm from './ExpenseForm.jsx';
import { Route, Routes } from 'react-router-dom';

const App = () => {
  const [projects, setProjects] = useState([]);
  const [expenses, setExpenses] = useState([]);

  useEffect(() => {
    fetch('http://localhost:5001/projects')
      .then((response) => response.json())
      .then((data) => setProjects(data))
      .catch((error) => console.error('Error fetching projects:', error));

    fetch('http://localhost:5001/expenses')
      .then((response) => response.json())
      .then((data) => setExpenses(data))
      .catch((error) => console.error('Error fetching expenses:', error));
  }, []);

  const addProject = (newProject) => {
    setProjects([...projects, newProject]);
  };

  const addExpense = (newExpense) => {
    setExpenses([...expenses, newExpense]);
  };

  const editProject = (updatedProject) => {
    setProjects(
      projects.map((project) =>
        project.id === updatedProject.id ? updatedProject : project
      )
    );
  };

  const editExpense = (updatedExpense) => {
    setExpenses(
      expenses.map((expense) =>
        expense.id === updatedExpense.id ? updatedExpense : expense
      )
    );
  };

  const deleteProject = (projectId) => {
    setProjects(projects.filter((project) => project.id !== projectId));
  };

  const deleteExpense = (expenseId) => {
    setExpenses(expenses.filter((expense) => expense.id !== expenseId));
  };

  return (
    <div className="App">
      <Header />
      <NavBar />
      <Routes>
        <Route path="/projects" element={<Dashboard projects={projects} />} />
        <Route path="/projects/new" element={<ProjectForm addProject={addProject} />} />
        <Route path="/expenses" element={<ExpenseDashboard expenses={expenses} />} />
        <Route path="/expenses/new" element={<ExpenseForm addExpense={addExpense} />} />
      </Routes>
    </div>
  );
};

export default App;
