import React, { useState } from 'react';

const ExpenseForm = ({ addExpense }) => {
  const [name, setName] = useState('');
  const [amount, setAmount] = useState('');
  const [projectId, setProjectId] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const newExpense = { name, amount, projectId };
    addExpense(newExpense);
    setName('');
    setAmount('');
    setProjectId('');
  };

  return (
    <form onSubmit={handleSubmit}>
      <input type="text" value={name} onChange={(e) => setName(e.target.value)} placeholder="Expense Name" required />
      <input type="number" value={amount} onChange={(e) => setAmount(e.target.value)} placeholder="Amount" required />
      <input type="number" value={projectId} onChange={(e) => setProjectId(e.target.value)} placeholder="Project ID" required />
      <button type="submit">Add Expense</button>
    </form>
  );
};

export default ExpenseForm;
